package Q548;

public class Main {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		for(int i=2;i<5;i++) {
			for(int j=1;j<6;j++) {
				System.out.print(i+" * "+ j+ " =");
				System.out.printf("%3d", (i*j));
				System.out.print("   ");
				
			}
			System.out.println("");
		}
		
	}

}
